              <p className="text-muted-foreground">{filteredVideos.length} videos in library</p>

              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredVideos.map((video) => (
                  <VideoCard
                    key={video.id}
