    const progress = await VideoProgress.findOneAndUpdate(
      { clientId, videoId },
      {
        watchedDuration,
        totalDuration,
        lastWatchedAt: new Date(),
        completed,
      },
      { upsert: true, new: true }
    );

    return progress;
  }

  async getContinueWatching(clientId: string): Promise<any[]> {
