server/models.ts.content:
import mongoose, { Schema, Document } from 'mongoose';

export interface IPackage extends Document {
  name: string;
  description?: string;
  price: number;
  features: string[];
  videoAccess: boolean;
  liveSessionsPerMonth: number;
  dietPlanAccess: boolean;
  workoutPlanAccess: boolean;
}

export interface IClient extends Document {
  name: string;
  phone: string;
  email?: string;
  packageId?: string;
  age?: number;
  gender?: string;
  height?: number;
  weight?: number;
  goal?: string;
  bio?: string;
  address?: string;
  profilePhoto?: string;
  medicalConditions?: string[];
  injuries?: string[];
  fitnessLevel?: 'beginner' | 'intermediate' | 'advanced';
  limitations?: string;
  language?: 'en' | 'hi';
  notificationPreferences?: {
    email: boolean;
    sessionReminders: boolean;
    achievements: boolean;
  };
  privacySettings?: {
    showEmail: boolean;
    showPhone: boolean;
    showProgress: boolean;
  };
  createdAt: Date;
}

export interface IBodyMetrics extends Document {
  clientId: string;
  weight: number;
  height: number;
  age: number;
  gender: string;
  bmi?: number;
  bmr?: number;
  tdee?: number;
  idealWeight?: number;
  targetCalories?: number;
  activityLevel: string;
  goal: string;
  recordedAt: Date;
}

export interface IVideo extends Document {
  title: string;
  description?: string;
  url: string;
  thumbnail?: string;
  category: string;
  duration?: number;
  intensity?: string;
  trainer?: string;
  packageRequirement?: string;
  createdAt: Date;
}

export interface IClientVideo extends Document {
  clientId: string;
  videoId: string;
  assignedAt: Date;
}

export interface IWorkoutPlan extends Document {
  clientId: string;
  name: string;
  description?: string;
  goal?: string;
  durationWeeks: number;
  exercises: any;
  createdAt: Date;
  updatedAt: Date;
}

export interface IDietPlan extends Document {
  clientId: string;
  name: string;
  targetCalories: number;
  protein?: number;
  carbs?: number;
  fats?: number;
  meals: any;
  allergens?: string[];
  waterIntakeGoal?: number;
  supplements?: Array<{
    name: string;
    dosage: string;
    timing: string;
  }>;
  createdAt: Date;
  updatedAt: Date;
}

export interface ILiveSession extends Document {
  title: string;
  description?: string;
  scheduledAt: Date;
  duration: number;
  meetingLink?: string;
  status: string;
  sessionType?: string;
  trainer?: string;
  maxParticipants?: number;
  currentParticipants?: number;
  createdAt: Date;
}

export interface ISessionClient extends Document {
  sessionId: string;
  clientId: string;
  attended: boolean;
}

export interface IWorkoutSession extends Document {
  clientId: string;
  workoutPlanId?: string;
  workoutName: string;
  duration: number;
  caloriesBurned: number;
  exercises: any;
  completedAt: Date;
  notes?: string;
}

export interface IVideoProgress extends Document {
  clientId: string;
  videoId: string;
  watchedDuration: number;
  totalDuration: number;
  lastWatchedAt: Date;
  completed: boolean;
}

export interface IVideoBookmark extends Document {
  clientId: string;
  videoId: string;
  bookmarkedAt: Date;
}

export interface IProgressPhoto extends Document {
  clientId: string;
  photoUrl: string;
  description?: string;
  weight?: number;
  uploadedAt: Date;
}

export interface IAchievement extends Document {
  clientId: string;
  type: string;
  title: string;
  description: string;
  unlockedAt: Date;
  metadata?: any;
}

const PackageSchema = new Schema({
  name: { type: String, required: true },
  description: String,
  price: { type: Number, required: true },
  features: [String],
  videoAccess: { type: Boolean, default: false },
  liveSessionsPerMonth: { type: Number, default: 0 },
  dietPlanAccess: { type: Boolean, default: false },
  workoutPlanAccess: { type: Boolean, default: false },
});

const ClientSchema = new Schema({
  name: { type: String, required: true },
  phone: { type: String, required: true, unique: true },
  email: String,
  packageId: { type: Schema.Types.ObjectId, ref: 'Package' },
  age: Number,
  gender: String,
  height: Number,
  weight: Number,
  goal: String,
  bio: String,
  address: String,
  profilePhoto: String,
  medicalConditions: [String],
  injuries: [String],
  fitnessLevel: { type: String, enum: ['beginner', 'intermediate', 'advanced'] },
  limitations: String,
  language: { type: String, enum: ['en', 'hi'], default: 'en' },
  notificationPreferences: {
    email: { type: Boolean, default: true },
    sessionReminders: { type: Boolean, default: true },
    achievements: { type: Boolean, default: true },
  },
  privacySettings: {
    showEmail: { type: Boolean, default: false },
    showPhone: { type: Boolean, default: false },
    showProgress: { type: Boolean, default: true },
  },
  createdAt: { type: Date, default: Date.now },
});

const BodyMetricsSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  weight: { type: Number, required: true },
  height: { type: Number, required: true },
  age: { type: Number, required: true },
  gender: { type: String, required: true },
  bmi: Number,
  bmr: Number,
  tdee: Number,
  idealWeight: Number,
  targetCalories: Number,
  activityLevel: { type: String, required: true },
  goal: { type: String, required: true },
  recordedAt: { type: Date, default: Date.now },
});

const VideoSchema = new Schema({
  title: { type: String, required: true },
  description: String,
  url: { type: String, required: true },
  thumbnail: String,
  category: { type: String, required: true },
  duration: Number,
  intensity: String,
  trainer: String,
  packageRequirement: { type: Schema.Types.ObjectId, ref: 'Package' },
  createdAt: { type: Date, default: Date.now },
});

const ClientVideoSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  videoId: { type: Schema.Types.ObjectId, ref: 'Video', required: true },
  assignedAt: { type: Date, default: Date.now },
});

const WorkoutPlanSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  name: { type: String, required: true },
  description: String,
  goal: String,
  durationWeeks: { type: Number, required: true },
  exercises: { type: Schema.Types.Mixed, required: true },
  createdAt: { type: Date, default: Date.now },
  updatedAt: { type: Date, default: Date.now },
});

const DietPlanSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  name: { type: String, required: true },
  targetCalories: { type: Number, required: true },
  protein: Number,
  carbs: Number,
  fats: Number,
  meals: { type: Schema.Types.Mixed, required: true },
  allergens: [String],
  waterIntakeGoal: Number,
  supplements: [{
    name: String,
    dosage: String,
    timing: String
  }],
  createdAt: { type: Date, default: Date.now },
  updatedAt: { type: Date, default: Date.now },
});

const LiveSessionSchema = new Schema({
  title: { type: String, required: true },
  description: String,
  scheduledAt: { type: Date, required: true },
  duration: { type: Number, required: true },
  meetingLink: String,
  status: { type: String, default: 'scheduled' },
  sessionType: String,
  trainer: String,
  maxParticipants: { type: Number, default: 15 },
  currentParticipants: { type: Number, default: 0 },
  createdAt: { type: Date, default: Date.now },
});

const SessionClientSchema = new Schema({
  sessionId: { type: Schema.Types.ObjectId, ref: 'LiveSession', required: true },
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  attended: { type: Boolean, default: false },
});

const WorkoutSessionSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  workoutPlanId: { type: Schema.Types.ObjectId, ref: 'WorkoutPlan' },
  workoutName: { type: String, required: true },
  duration: { type: Number, required: true },
  caloriesBurned: { type: Number, required: true },
  exercises: { type: Schema.Types.Mixed },
  completedAt: { type: Date, default: Date.now },
  notes: String,
});

const VideoProgressSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  videoId: { type: Schema.Types.ObjectId, ref: 'Video', required: true },
  watchedDuration: { type: Number, required: true, default: 0 },
  totalDuration: { type: Number, required: true },
  lastWatchedAt: { type: Date, default: Date.now },
  completed: { type: Boolean, default: false },
});

const VideoBookmarkSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  videoId: { type: Schema.Types.ObjectId, ref: 'Video', required: true },
  bookmarkedAt: { type: Date, default: Date.now },
});

const ProgressPhotoSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  photoUrl: { type: String, required: true },
  description: String,
  weight: Number,
  uploadedAt: { type: Date, default: Date.now },
});

const AchievementSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  type: { type: String, required: true },
  title: { type: String, required: true },
  description: { type: String, required: true },
  unlockedAt: { type: Date, default: Date.now },
  metadata: Schema.Types.Mixed,
});

export interface IGoal extends Document {
  clientId: string;
  goalType: 'weight' | 'fitness' | 'nutrition';
  title: string;
  description?: string;
  targetValue: number;
  currentValue: number;
  unit: string;
  targetDate?: Date;
  status: 'active' | 'completed' | 'abandoned';
  progress: number;
  milestones: Array<{
    value: number;
    label: string;
    achieved: boolean;
    achievedAt?: Date;
  }>;
  createdAt: Date;
  updatedAt: Date;
}

const GoalSchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  goalType: { type: String, enum: ['weight', 'fitness', 'nutrition'], required: true },
  title: { type: String, required: true },
  description: String,
  targetValue: { type: Number, required: true },
  currentValue: { type: Number, required: true, default: 0 },
  unit: { type: String, required: true },
  targetDate: Date,
  status: { type: String, enum: ['active', 'completed', 'abandoned'], default: 'active' },
  progress: { type: Number, default: 0 },
  milestones: [{
    value: { type: Number, required: true },
    label: { type: String, required: true },
    achieved: { type: Boolean, default: false },
    achievedAt: Date,
  }],
  createdAt: { type: Date, default: Date.now },
  updatedAt: { type: Date, default: Date.now },
});

export interface IPaymentHistory extends Document {
  clientId: string;
  amount: number;
  currency: string;
  status: 'pending' | 'completed' | 'failed' | 'refunded';
  transactionId?: string;
  invoiceNumber: string;
  paymentMethod: string;
  packageId?: string;
  packageName?: string;
  billingDate: Date;
  nextBillingDate?: Date;
  receiptUrl?: string;
  createdAt: Date;
}

const PaymentHistorySchema = new Schema({
  clientId: { type: Schema.Types.ObjectId, ref: 'Client', required: true },
  amount: { type: Number, required: true },
  currency: { type: String, default: 'USD' },
  status: { type: String, enum: ['pending', 'completed', 'failed', 'refunded'], default: 'completed' },
  transactionId: String,
  invoiceNumber: { type: String, required: true },
  paymentMethod: { type: String, required: true },
  packageId: { type: Schema.Types.ObjectId, ref: 'Package' },
  packageName: String,
  billingDate: { type: Date, required: true },
  nextBillingDate: Date,
  receiptUrl: String,
  createdAt: { type: Date, default: Date.now },
});

export const Package = mongoose.model<IPackage>('Package', PackageSchema);
export const Client = mongoose.model<IClient>('Client', ClientSchema);
export const BodyMetrics = mongoose.model<IBodyMetrics>('BodyMetrics', BodyMetricsSchema);
export const Video = mongoose.model<IVideo>('Video', VideoSchema);
export const ClientVideo = mongoose.model<IClientVideo>('ClientVideo', ClientVideoSchema);
export const WorkoutPlan = mongoose.model<IWorkoutPlan>('WorkoutPlan', WorkoutPlanSchema);
export const DietPlan = mongoose.model<IDietPlan>('DietPlan', DietPlanSchema);
export const LiveSession = mongoose.model<ILiveSession>('LiveSession', LiveSessionSchema);
export const SessionClient = mongoose.model<ISessionClient>('SessionClient', SessionClientSchema);
export const WorkoutSession = mongoose.model<IWorkoutSession>('WorkoutSession', WorkoutSessionSchema);
export const VideoProgress = mongoose.model<IVideoProgress>('VideoProgress', VideoProgressSchema);
export const VideoBookmark = mongoose.model<IVideoBookmark>('VideoBookmark', VideoBookmarkSchema);
export const ProgressPhoto = mongoose.model<IProgressPhoto>('ProgressPhoto', ProgressPhotoSchema);
export const Achievement = mongoose.model<IAchievement>('Achievement', AchievementSchema);
export const Goal = mongoose.model<IGoal>('Goal', GoalSchema);
export const PaymentHistory = mongoose.model<IPaymentHistory>('PaymentHistory', PaymentHistorySchema);

tailwind.config.ts.content:
import type { Config } from "tailwindcss";

export default {
  darkMode: ["class"],
  content: ["./client/index.html", "./client/src/**/*.{js,jsx,ts,tsx}"],
  theme: {
    extend: {
      borderRadius: {
        lg: ".5625rem",
        md: ".375rem",
        sm: ".1875rem",
      },
      colors: {
        background: "hsl(var(--background) / <alpha-value>)",
        foreground: "hsl(var(--foreground) / <alpha-value>)",
        border: "hsl(var(--border) / <alpha-value>)",
        input: "hsl(var(--input) / <alpha-value>)",
        card: {
          DEFAULT: "hsl(var(--card) / <alpha-value>)",
          foreground: "hsl(var(--card-foreground) / <alpha-value>)",
          border: "hsl(var(--card-border) / <alpha-value>)",
        },
        popover: {
          DEFAULT: "hsl(var(--popover) / <alpha-value>)",
          foreground: "hsl(var(--popover-foreground) / <alpha-value>)",
          border: "hsl(var(--popover-border) / <alpha-value>)",
        },
        primary: {
          DEFAULT: "hsl(var(--primary) / <alpha-value>)",
          foreground: "hsl(var(--primary-foreground) / <alpha-value>)",
          border: "var(--primary-border)",
        },
        secondary: {
          DEFAULT: "hsl(var(--secondary) / <alpha-value>)",
          foreground: "hsl(var(--secondary-foreground) / <alpha-value>)",
          border: "var(--secondary-border)",
        },
        muted: {
          DEFAULT: "hsl(var(--muted) / <alpha-value>)",
          foreground: "hsl(var(--muted-foreground) / <alpha-value>)",
          border: "var(--muted-border)",
        },
        accent: {
          DEFAULT: "hsl(var(--accent) / <alpha-value>)",
          foreground: "hsl(var(--accent-foreground) / <alpha-value>)",
          border: "var(--accent-border)",
        },
        destructive: {
          DEFAULT: "hsl(var(--destructive) / <alpha-value>)",
          foreground: "hsl(var(--destructive-foreground) / <alpha-value>)",
          border: "var(--destructive-border)",
        },
        ring: "hsl(var(--ring) / <alpha-value>)",
        chart: {
          "1": "hsl(var(--chart-1) / <alpha-value>)",
          "2": "hsl(var(--chart-2) / <alpha-value>)",
          "3": "hsl(var(--chart-3) / <alpha-value>)",
          "4": "hsl(var(--chart-4) / <alpha-value>)",
          "5": "hsl(var(--chart-5) / <alpha-value>)",
        },
        sidebar: {
          ring: "hsl(var(--sidebar-ring) / <alpha-value>)",
          DEFAULT: "hsl(var(--sidebar) / <alpha-value>)",
          foreground: "hsl(var(--sidebar-foreground) / <alpha-value>)",
          border: "hsl(var(--sidebar-border) / <alpha-value>)",
        },
        "sidebar-primary": {
          DEFAULT: "hsl(var(--sidebar-primary) / <alpha-value>)",
          foreground: "hsl(var(--sidebar-primary-foreground) / <alpha-value>)",
          border: "var(--sidebar-primary-border)",
        },
        "sidebar-accent": {
          DEFAULT: "hsl(var(--sidebar-accent) / <alpha-value>)",
          foreground: "hsl(var(--sidebar-accent-foreground) / <alpha-value>)",
          border: "var(--sidebar-accent-border)"
        },
        status: {
          online: "rgb(34 197 94)",
          away: "rgb(245 158 11)",
          busy: "rgb(239 68 68)",
          offline: "rgb(156 163 175)",
        },
      },
      fontFamily: {
        sans: ["var(--font-sans)"],
        serif: ["var(--font-serif)"],
        mono: ["var(--font-mono)"],
        display: ["var(--font-display)", "var(--font-sans)"],
      },
      keyframes: {
        "accordion-down": {
          from: { height: "0" },
          to: { height: "var(--radix-accordion-content-height)" },
        },
        "accordion-up": {
          from: { height: "var(--radix-accordion-content-height)" },
          to: { height: "0" },
        },
      },
      animation: {
        "accordion-down": "accordion-down 0.2s ease-out",
        "accordion-up": "accordion-up 0.2s ease-out",
      },
    },
  },
  plugins: [require("tailwindcss-animate"), require("@tailwindcss/typography")],
} satisfies Config;

