import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useState } from "react";

interface AddClientModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function AddClientModal({ open, onOpenChange }: AddClientModalProps) {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    package: "",
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log("Adding new client:", formData);
    // Reset form
    setFormData({ firstName: "", lastName: "", email: "", phone: "", package: "" });
    onOpenChange(false);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle className="font-display text-2xl">Add New Client</DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="firstName">First Name *</Label>
              <Input
                id="firstName"
                required
                value={formData.firstName}
                onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                data-testid="input-first-name"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="lastName">Last Name *</Label>
              <Input
                id="lastName"
                required
                value={formData.lastName}
                onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                data-testid="input-last-name"
              />
            </div>
          </div>
          <div className="space-y-2">
            <Label htmlFor="email">Email *</Label>
            <Input
              id="email"
              type="email"
              required
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              data-testid="input-email"
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="phone">Phone Number *</Label>
            <Input
              id="phone"
              type="tel"
              required
              value={formData.phone}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              data-testid="input-phone"
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="package">Package *</Label>
            <Select value={formData.package} onValueChange={(value) => setFormData({ ...formData, package: value })}>
              <SelectTrigger data-testid="select-package">
                <SelectValue placeholder="Select a package" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="Basic">Basic - $29/month</SelectItem>
                <SelectItem value="Premium">Premium - $59/month</SelectItem>
                <SelectItem value="Elite">Elite - $99/month</SelectItem>
              </SelectContent>
            </Select>
          </div>
          <div className="flex gap-3 pt-4">
            <Button type="button" variant="outline" className="flex-1" onClick={() => onOpenChange(false)} data-testid="button-cancel-add-client">
              Cancel
            </Button>
            <Button type="submit" className="flex-1" data-testid="button-submit-add-client">
              Add Client
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/diet-plan-card.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";

interface MealItem {
  time: string;
  name: string;
  calories: number;
  protein: number;
  carbs: number;
  fats: number;
}

interface DietPlanCardProps {
  day: string;
  meals: MealItem[];
  totalCalories: number;
}

export function DietPlanCard({ day, meals, totalCalories }: DietPlanCardProps) {
  return (
    <Card data-testid={`card-diet-${day.toLowerCase()}`}>
      <CardHeader className="flex flex-row items-center justify-between gap-4 space-y-0">
        <CardTitle className="font-display">{day}</CardTitle>
        <Badge data-testid="badge-total-calories">
          {totalCalories} cal
        </Badge>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {meals.map((meal, index) => (
            <div
              key={index}
              className="border-l-2 border-primary pl-4 py-2"
              data-testid={`item-meal-${index}`}
            >
              <div className="flex items-start justify-between gap-4 mb-1">
                <div className="flex-1">
                  <p className="text-xs text-muted-foreground">{meal.time}</p>
                  <p className="font-semibold" data-testid="text-meal-name">{meal.name}</p>
                </div>
                <Badge variant="secondary" data-testid="text-meal-calories">
                  {meal.calories} cal
                </Badge>
              </div>
              <div className="flex gap-4 text-xs text-muted-foreground mt-2">
                <span data-testid="text-protein">P: {meal.protein}g</span>
                <span data-testid="text-carbs">C: {meal.carbs}g</span>
                <span data-testid="text-fats">F: {meal.fats}g</span>
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

