shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp, jsonb, decimal, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const packages = pgTable("packages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  price: decimal("price", { precision: 10, scale: 2 }).notNull(),
  features: text("features").array().notNull(),
  videoAccess: boolean("video_access").notNull().default(false),
  liveSessionsPerMonth: integer("live_sessions_per_month").notNull().default(0),
  dietPlanAccess: boolean("diet_plan_access").notNull().default(false),
  workoutPlanAccess: boolean("workout_plan_access").notNull().default(false),
});

export const insertPackageSchema = createInsertSchema(packages).omit({ id: true });
export type InsertPackage = z.infer<typeof insertPackageSchema>;
export type Package = typeof packages.$inferSelect;

export const clients = pgTable("clients", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  phone: text("phone").notNull().unique(),
  email: text("email"),
  packageId: varchar("package_id").references(() => packages.id),
  age: integer("age"),
  gender: text("gender"),
  height: decimal("height", { precision: 5, scale: 2 }),
  weight: decimal("weight", { precision: 5, scale: 2 }),
  goal: text("goal"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export const insertClientSchema = createInsertSchema(clients).omit({ id: true, createdAt: true });
export type InsertClient = z.infer<typeof insertClientSchema>;
export type Client = typeof clients.$inferSelect;

export const bodyMetrics = pgTable("body_metrics", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  weight: decimal("weight", { precision: 5, scale: 2 }).notNull(),
  height: decimal("height", { precision: 5, scale: 2 }).notNull(),
  age: integer("age").notNull(),
  gender: text("gender").notNull(),
  bmi: decimal("bmi", { precision: 5, scale: 2 }),
  bmr: decimal("bmr", { precision: 7, scale: 2 }),
  tdee: decimal("tdee", { precision: 7, scale: 2 }),
  idealWeight: decimal("ideal_weight", { precision: 5, scale: 2 }),
  targetCalories: decimal("target_calories", { precision: 7, scale: 2 }),
  activityLevel: text("activity_level").notNull(),
  goal: text("goal").notNull(),
  recordedAt: timestamp("recorded_at").notNull().default(sql`now()`),
});

export const insertBodyMetricsSchema = createInsertSchema(bodyMetrics).omit({ id: true, recordedAt: true });
export type InsertBodyMetrics = z.infer<typeof insertBodyMetricsSchema>;
export type BodyMetrics = typeof bodyMetrics.$inferSelect;

export const videos = pgTable("videos", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description"),
  url: text("url").notNull(),
  thumbnail: text("thumbnail"),
  category: text("category").notNull(),
  duration: integer("duration"),
  packageRequirement: varchar("package_requirement").references(() => packages.id),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export const insertVideoSchema = createInsertSchema(videos).omit({ id: true, createdAt: true });
export type InsertVideo = z.infer<typeof insertVideoSchema>;
export type Video = typeof videos.$inferSelect;

export const clientVideos = pgTable("client_videos", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  videoId: varchar("video_id").notNull().references(() => videos.id),
  assignedAt: timestamp("assigned_at").notNull().default(sql`now()`),
});

export const insertClientVideoSchema = createInsertSchema(clientVideos).omit({ id: true, assignedAt: true });
export type InsertClientVideo = z.infer<typeof insertClientVideoSchema>;
export type ClientVideo = typeof clientVideos.$inferSelect;

export const workoutPlans = pgTable("workout_plans", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  name: text("name").notNull(),
  description: text("description"),
  goal: text("goal"),
  durationWeeks: integer("duration_weeks").notNull(),
  exercises: jsonb("exercises").notNull(),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

export const insertWorkoutPlanSchema = createInsertSchema(workoutPlans).omit({ id: true, createdAt: true, updatedAt: true });
export type InsertWorkoutPlan = z.infer<typeof insertWorkoutPlanSchema>;
export type WorkoutPlan = typeof workoutPlans.$inferSelect;

export const dietPlans = pgTable("diet_plans", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  name: text("name").notNull(),
  targetCalories: decimal("target_calories", { precision: 7, scale: 2 }).notNull(),
  protein: decimal("protein", { precision: 5, scale: 2 }),
  carbs: decimal("carbs", { precision: 5, scale: 2 }),
  fats: decimal("fats", { precision: 5, scale: 2 }),
  meals: jsonb("meals").notNull(),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
  updatedAt: timestamp("updated_at").notNull().default(sql`now()`),
});

export const insertDietPlanSchema = createInsertSchema(dietPlans).omit({ id: true, createdAt: true, updatedAt: true });
export type InsertDietPlan = z.infer<typeof insertDietPlanSchema>;
export type DietPlan = typeof dietPlans.$inferSelect;

export const liveSessions = pgTable("live_sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description"),
  scheduledAt: timestamp("scheduled_at").notNull(),
  duration: integer("duration").notNull(),
  meetingLink: text("meeting_link"),
  status: text("status").notNull().default('scheduled'),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export const insertLiveSessionSchema = createInsertSchema(liveSessions).omit({ id: true, createdAt: true });
export type InsertLiveSession = z.infer<typeof insertLiveSessionSchema>;
export type LiveSession = typeof liveSessions.$inferSelect;

export const sessionClients = pgTable("session_clients", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sessionId: varchar("session_id").notNull().references(() => liveSessions.id),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  attended: boolean("attended").default(false),
});

export const insertSessionClientSchema = createInsertSchema(sessionClients).omit({ id: true });
export type InsertSessionClient = z.infer<typeof insertSessionClientSchema>;
export type SessionClient = typeof sessionClients.$inferSelect;

export const weightTracking = pgTable("weight_tracking", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  weight: decimal("weight", { precision: 5, scale: 2 }).notNull(),
  date: timestamp("date").notNull().default(sql`now()`),
});

export const insertWeightTrackingSchema = createInsertSchema(weightTracking).omit({ id: true });
export type InsertWeightTracking = z.infer<typeof insertWeightTrackingSchema>;
export type WeightTracking = typeof weightTracking.$inferSelect;

export const bodyMeasurements = pgTable("body_measurements", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  chest: decimal("chest", { precision: 5, scale: 2 }),
  waist: decimal("waist", { precision: 5, scale: 2 }),
  hips: decimal("hips", { precision: 5, scale: 2 }),
  leftArm: decimal("left_arm", { precision: 5, scale: 2 }),
  rightArm: decimal("right_arm", { precision: 5, scale: 2 }),
  leftThigh: decimal("left_thigh", { precision: 5, scale: 2 }),
  rightThigh: decimal("right_thigh", { precision: 5, scale: 2 }),
  neck: decimal("neck", { precision: 5, scale: 2 }),
  shoulders: decimal("shoulders", { precision: 5, scale: 2 }),
  date: timestamp("date").notNull().default(sql`now()`),
});

export const insertBodyMeasurementsSchema = createInsertSchema(bodyMeasurements).omit({ id: true });
export type InsertBodyMeasurements = z.infer<typeof insertBodyMeasurementsSchema>;
export type BodyMeasurements = typeof bodyMeasurements.$inferSelect;

export const personalRecords = pgTable("personal_records", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  category: text("category").notNull(),
  value: decimal("value", { precision: 10, scale: 2 }).notNull(),
  date: timestamp("date").notNull().default(sql`now()`),
});

export const insertPersonalRecordSchema = createInsertSchema(personalRecords).omit({ id: true });
export type InsertPersonalRecord = z.infer<typeof insertPersonalRecordSchema>;
export type PersonalRecord = typeof personalRecords.$inferSelect;

export const weeklyWorkoutGoals = pgTable("weekly_workout_goals", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  clientId: varchar("client_id").notNull().references(() => clients.id),
  plannedWorkouts: integer("planned_workouts").notNull().default(5),
  weekStartDate: timestamp("week_start_date").notNull(),
});

export const insertWeeklyWorkoutGoalSchema = createInsertSchema(weeklyWorkoutGoals).omit({ id: true });
export type InsertWeeklyWorkoutGoal = z.infer<typeof insertWeeklyWorkoutGoalSchema>;
export type WeeklyWorkoutGoal = typeof weeklyWorkoutGoals.$inferSelect;

client/src/pages/client-profile.tsx.content:
import { Button } from "@/components/ui/button";
import { ClientHeader } from "@/components/client-header";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Mail, Phone, MapPin, CreditCard, FileText, Download, Globe, Shield, Heart, Activity, Moon, Sun } from "lucide-react";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useState, useEffect } from "react";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLanguage } from "@/lib/language-context";

export default function ClientProfile() {
  const [, setLocation] = useLocation();
  const [clientId, setClientId] = useState<string | null>(null);
  const { toast } = useToast();
  const { language: currentLanguage, setLanguage, t } = useLanguage();
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });

  // Form state
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    bio: '',
    fitnessLevel: 'beginner' as 'beginner' | 'intermediate' | 'advanced',
    medicalConditions: '',
    injuries: '',
    limitations: '',
    language: 'en' as 'en' | 'hi',
    emailNotifications: true,
    sessionReminders: true,
    achievementNotifications: true,
    showEmail: false,
    showPhone: false,
    showProgress: true,
  });

  useEffect(() => {
    const id = localStorage.getItem('clientId');
    if (!id) {
      setLocation('/client-access');
    } else {
      setClientId(id);
    }
  }, [setLocation]);

  const { data: client } = useQuery<any>({
    queryKey: ['/api/clients', clientId],
    enabled: !!clientId,
  });

  const { data: payments = [] } = useQuery<any[]>({
    queryKey: ['/api/payment-history', clientId],
    enabled: !!clientId,
  });

  // Initialize form data when client loads
  useEffect(() => {
    if (client) {
      setFormData({
        name: client.name || '',
        email: client.email || '',
        phone: client.phone || '',
        address: client.address || '',
        bio: client.bio || '',
        fitnessLevel: client.fitnessLevel || 'beginner',
        medicalConditions: client.medicalConditions?.join(', ') || '',
        injuries: client.injuries?.join(', ') || '',
        limitations: client.limitations || '',
        language: client.language || currentLanguage,
        emailNotifications: client.notificationPreferences?.email ?? true,
        sessionReminders: client.notificationPreferences?.sessionReminders ?? true,
        achievementNotifications: client.notificationPreferences?.achievements ?? true,
        showEmail: client.privacySettings?.showEmail ?? false,
        showPhone: client.privacySettings?.showPhone ?? false,
        showProgress: client.privacySettings?.showProgress ?? true,
      });
    }
  }, [client, currentLanguage]);

  const toggleDarkMode = () => {
    const newMode = !darkMode;
    setDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
    document.documentElement.classList.toggle("dark", newMode);
  };

  const updateClientMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest('PATCH', `/api/clients/${clientId}`, data);
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/clients', clientId] });
      // Sync language with provider if language was updated
      if (variables.language) {
        setLanguage(variables.language);
      }
      toast({
        title: t('common.success'),
        description: t('msg.profileUpdated'),
      });
    },
    onError: () => {
      toast({
        title: t('common.error'),
        description: t('msg.error'),
        variant: "destructive",
      });
    },
  });

  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  if (!client) {
    return <div className="flex items-center justify-center min-h-screen">{t('common.loading')}</div>;
  }

  return (
    <div className="min-h-screen flex flex-col">
      <ClientHeader currentPage="profile" />

      <main className="flex-1 py-8">
        <div className="container mx-auto px-6 max-w-4xl space-y-8">
          <div className="flex items-center gap-6">
            <Avatar className="h-24 w-24">
              <AvatarFallback className="bg-primary text-primary-foreground text-3xl">
                {getInitials(client.name)}
              </AvatarFallback>
            </Avatar>
            <div className="flex-1">
              <h1 className="text-3xl font-display font-bold tracking-tight">{client.name}</h1>
              <p className="text-muted-foreground mt-1">{t('profile.memberSince')} {new Date(client.createdAt).toLocaleDateString(currentLanguage === 'hi' ? 'hi-IN' : 'en-US', { month: 'short', day: 'numeric', year: 'numeric' })}</p>
              <div className="flex items-center gap-2 mt-2">
                <Badge className="bg-chart-2">{client.packageId?.name || t('common.no')} {t('profile.plan')}</Badge>
                <Badge variant="outline" className="bg-chart-3 text-white">{t('profile.active')}</Badge>
              </div>
            </div>
            <Button data-testid="button-edit-profile">{t('profile.editProfile')}</Button>
          </div>

          <Tabs defaultValue="personal" className="w-full">
            <TabsList className="grid w-full grid-cols-6">
              <TabsTrigger value="personal">{t('tabs.personal')}</TabsTrigger>
              <TabsTrigger value="health">{t('tabs.health')}</TabsTrigger>
              <TabsTrigger value="subscription">{t('tabs.subscription')}</TabsTrigger>
              <TabsTrigger value="payments">{t('tabs.payments')}</TabsTrigger>
              <TabsTrigger value="preferences">{t('tabs.preferences')}</TabsTrigger>
              <TabsTrigger value="privacy">{t('tabs.privacy')}</TabsTrigger>
            </TabsList>

            <TabsContent value="personal" className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle>Contact Information</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="fullName">Full Name</Label>
                    <Input 
                      id="fullName" 
                      value={formData.name} 
                      onChange={(e) => setFormData({...formData, name: e.target.value})}
                      data-testid="input-full-name" 
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="email">Email</Label>
                    <div className="relative">
                      <Mail className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                      <Input 
                        id="email" 
                        className="pl-10" 
                        value={formData.email} 
                        onChange={(e) => setFormData({...formData, email: e.target.value})}
                        data-testid="input-email" 
                      />
                    </div>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="phone">Phone Number</Label>
                    <div className="relative">
                      <Phone className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                      <Input 
                        id="phone" 
                        className="pl-10" 
                        value={formData.phone} 
                        onChange={(e) => setFormData({...formData, phone: e.target.value})}
                        data-testid="input-phone" 
                      />
                    </div>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="address">Address</Label>
                    <div className="relative">
                      <MapPin className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                      <Input 
                        id="address" 
                        className="pl-10" 
                        value={formData.address} 
                        onChange={(e) => setFormData({...formData, address: e.target.value})}
                        data-testid="input-address" 
                      />
                    </div>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="bio">Bio</Label>
                    <Textarea 
                      id="bio" 
                      placeholder="Tell us about yourself, your fitness goals..." 
                      value={formData.bio} 
                      onChange={(e) => setFormData({...formData, bio: e.target.value})}
                      className="min-h-24"
                      data-testid="textarea-bio" 
                    />
                  </div>
                  <Button 
                    className="w-full" 
                    onClick={() => {
                      updateClientMutation.mutate({
                        name: formData.name,
                        email: formData.email,
                        phone: formData.phone,
                        address: formData.address,
                        bio: formData.bio,
                      });
                    }}
                    disabled={updateClientMutation.isPending}
                    data-testid="button-save-personal"
                  >
                    {updateClientMutation.isPending ? "Saving..." : "Save Changes"}
                  </Button>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="health" className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Heart className="h-5 w-5" />
                    Health Profile
                  </CardTitle>
                  <CardDescription>Manage your health information and fitness level</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="fitnessLevel">Fitness Level</Label>
                    <Select value={formData.fitnessLevel} onValueChange={(value: any) => setFormData({...formData, fitnessLevel: value})}>
                      <SelectTrigger id="fitnessLevel" data-testid="select-fitness-level">
                        <SelectValue placeholder="Select your fitness level" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="beginner">Beginner</SelectItem>
                        <SelectItem value="intermediate">Intermediate</SelectItem>
                        <SelectItem value="advanced">Advanced</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="medicalConditions">Medical Conditions</Label>
                    <Textarea
                      id="medicalConditions"
                      placeholder="List any medical conditions (e.g., diabetes, hypertension...)"
                      value={formData.medicalConditions}
                      onChange={(e) => setFormData({...formData, medicalConditions: e.target.value})}
                      className="min-h-20"
                      data-testid="textarea-medical-conditions"
                    />
                    <p className="text-sm text-muted-foreground">Separate multiple conditions with commas</p>
                  </div>
                  <div className="space-y-2">
