import { ClientHeader } from "@/components/client-header";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Calendar, Target, TrendingUp, CheckCircle2 } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { format, startOfWeek, endOfWeek, eachDayOfInterval, isSameDay } from "date-fns";

export default function ClientWeeklyCompletion() {
  const { data: weeklyData, isLoading } = useQuery({
    queryKey: ['/api/progress/weekly-completion'],
  });

  const currentWeekStart = startOfWeek(new Date(), { weekStartsOn: 1 });
  const currentWeekEnd = endOfWeek(new Date(), { weekStartsOn: 1 });
  const daysOfWeek = eachDayOfInterval({ start: currentWeekStart, end: currentWeekEnd });

  const completedDays = weeklyData?.completedDays || [];
  const plannedWorkouts = weeklyData?.plannedWorkouts || 5;
  const completedWorkouts = weeklyData?.completedWorkouts || 0;
  const completionRate = plannedWorkouts > 0 ? (completedWorkouts / plannedWorkouts) * 100 : 0;

  const isCompleted = (date: Date) => {
    return completedDays.some((d: string) => isSameDay(new Date(d), date));
  };

  return (
    <div className="min-h-screen flex flex-col bg-background">
      <ClientHeader currentPage="weekly-completion" />
      <main className="flex-1 container mx-auto px-6 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-display font-bold tracking-tight mb-2">Weekly Workout Completion</h1>
          <p className="text-muted-foreground">
            Track your weekly workout adherence and stay consistent
          </p>
        </div>

        <div className="grid gap-6 md:grid-cols-3 mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Completed This Week</CardTitle>
              <CheckCircle2 className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-3xl font-bold">
                {completedWorkouts} / {plannedWorkouts}
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                {completionRate.toFixed(0)}% completion rate
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">This Week's Goal</CardTitle>
              <Target className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-3xl font-bold">{plannedWorkouts}</div>
              <p className="text-xs text-muted-foreground mt-1">Planned workouts</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between gap-1 space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Weekly Average</CardTitle>
              <TrendingUp className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-3xl font-bold">
                {weeklyData?.average || 0}
              </div>
              <p className="text-xs text-muted-foreground mt-1">Workouts per week</p>
            </CardContent>
          </Card>
        </div>

        <Card className="mb-8">
          <CardHeader>
            <CardTitle>This Week's Progress</CardTitle>
            <CardDescription>
              {format(currentWeekStart, 'MMM dd')} - {format(currentWeekEnd, 'MMM dd, yyyy')}
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <div className="flex items-center justify-between mb-2 text-sm">
                <span className="text-muted-foreground">Completion</span>
                <span className="font-medium">{completionRate.toFixed(0)}%</span>
              </div>
              <Progress value={completionRate} className="h-3" />
            </div>

            <div className="grid grid-cols-7 gap-2 mt-6">
              {daysOfWeek.map((day, index) => {
                const completed = isCompleted(day);
                const isToday = isSameDay(day, new Date());

                return (
                  <div
                    key={index}
                    className={`p-4 rounded-md border text-center ${
                      completed
                        ? 'bg-primary text-primary-foreground border-primary'
                        : isToday
                        ? 'border-primary border-2'
                        : ''
                    }`}
                    data-testid={`day-${index}`}
                  >
                    <div className="text-xs font-medium mb-1">
                      {format(day, 'EEE')}
                    </div>
                    <div className="text-2xl font-bold mb-2">
                      {format(day, 'd')}
                    </div>
                    {completed && (
                      <CheckCircle2 className="h-5 w-5 mx-auto" data-testid={`completed-${index}`} />
                    )}
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Weekly History</CardTitle>
            <CardDescription>Your workout completion over recent weeks</CardDescription>
          </CardHeader>
          <CardContent>
            {weeklyData?.history && weeklyData.history.length > 0 ? (
              <div className="space-y-3">
                {weeklyData.history.map((week: any, index: number) => (
                  <div key={index} className="flex items-center justify-between py-3 border-b last:border-0" data-testid={`week-${index}`}>
                    <div>
                      <div className="font-medium">Week of {format(new Date(week.startDate), 'MMM dd, yyyy')}</div>
                      <div className="text-sm text-muted-foreground">
                        {week.completed} / {week.planned} workouts completed
                      </div>
                    </div>
                    <div className="flex items-center gap-3">
                      <div className="text-sm font-medium">
                        {((week.completed / week.planned) * 100).toFixed(0)}%
                      </div>
                      <div className="w-24">
                        <Progress value={(week.completed / week.planned) * 100} className="h-2" />
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center text-muted-foreground py-8">
                <Calendar className="h-12 w-12 mx-auto mb-3 opacity-50" />
                <p>No weekly history available yet</p>
              </div>
            )}
          </CardContent>
        </Card>
      </main>
    </div>
  );
}

client/src/pages/client-goals.tsx.content:
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { ClientHeader } from "@/components/client-header";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import {
  Target,
  Plus,
  TrendingUp,
  Calendar,
  Edit,
  Trash2,
  Award,
  CheckCircle2,
} from "lucide-react";
import { format } from "date-fns";

interface Goal {
  _id: string;
  clientId: string;
  goalType: 'weight' | 'fitness' | 'nutrition';
  title: string;
  description?: string;
  targetValue: number;
  currentValue: number;
  unit: string;
  targetDate?: string;
  status: 'active' | 'completed' | 'abandoned';
  progress: number;
  milestones: Array<{
    value: number;
    label: string;
    achieved: boolean;
    achievedAt?: string;
  }>;
  createdAt: string;
  updatedAt: string;
}

const goalFormSchema = z.object({
  goalType: z.enum(['weight', 'fitness', 'nutrition']),
  title: z.string().min(1, "Title is required"),
  description: z.string().optional(),
  targetValue: z.coerce.number().min(0.01, "Target value must be greater than 0"),
  currentValue: z.coerce.number().min(0, "Current value must be 0 or greater"),
  unit: z.string().min(1, "Unit is required"),
  targetDate: z.string().optional(),
});

type GoalFormData = z.infer<typeof goalFormSchema>;

export default function ClientGoals() {
  const [, setLocation] = useLocation();
  const [clientId, setClientId] = useState<string | null>(null);
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [editingGoal, setEditingGoal] = useState<Goal | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    const id = localStorage.getItem('clientId');
    if (!id) {
      setLocation('/client-access');
    } else {
      setClientId(id);
    }
  }, [setLocation]);

  const { data: goals, isLoading } = useQuery<Goal[]>({
    queryKey: ['/api/goals', clientId],
    enabled: !!clientId,
  });

  const form = useForm<GoalFormData>({
    resolver: zodResolver(goalFormSchema),
    defaultValues: {
      goalType: 'weight',
      title: '',
      description: '',
      targetValue: 0,
      currentValue: 0,
      unit: 'kg',
      targetDate: '',
    },
  });

  const createGoalMutation = useMutation({
    mutationFn: async (data: GoalFormData) => {
      const response = await apiRequest('POST', '/api/goals', {
        ...data,
        clientId,
        milestones: generateMilestones(data.targetValue, data.unit),
      });
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/goals', clientId] });
      setIsCreateOpen(false);
      form.reset();
      toast({
        title: "Goal created",
        description: "Your new goal has been created successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create goal",
        variant: "destructive",
      });
    },
  });

  const updateGoalMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<GoalFormData> }) => {
      const response = await apiRequest('PATCH', `/api/goals/${id}`, data);
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/goals', clientId] });
      setEditingGoal(null);
      toast({
        title: "Goal updated",
        description: "Your goal has been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update goal",
        variant: "destructive",
      });
    },
  });

  const deleteGoalMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest('DELETE', `/api/goals/${id}`);
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/goals', clientId] });
      toast({
        title: "Goal deleted",
        description: "Your goal has been deleted successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete goal",
        variant: "destructive",
      });
    },
  });

  const updateProgressMutation = useMutation({
    mutationFn: async ({ id, currentValue }: { id: string; currentValue: number }) => {
      const response = await apiRequest('POST', `/api/goals/${id}/progress`, { currentValue });
      return await response.json();
    },
    onSuccess: (data: Goal) => {
      queryClient.invalidateQueries({ queryKey: ['/api/goals', clientId] });

      const newMilestones = data.milestones.filter(m => 
        m.achieved && new Date(m.achievedAt || '').getTime() > Date.now() - 5000
      );

      if (newMilestones.length > 0) {
        newMilestones.forEach(milestone => {
          toast({
            title: "Milestone Achieved!",
            description: milestone.label,
          });
        });
      }

      if (data.status === 'completed') {
        toast({
          title: "Goal Completed!",
          description: `Congratulations! You've achieved your goal: ${data.title}`,
        });
      }
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update progress",
        variant: "destructive",
      });
    },
  });

  function generateMilestones(targetValue: number, unit: string) {
    const milestones = [];
    const steps = [0.25, 0.5, 0.75, 1.0];

    for (const step of steps) {
      milestones.push({
        value: targetValue * step,
        label: `Reached ${Math.round(targetValue * step)}${unit} (${step * 100}% complete)`,
        achieved: false,
      });
    }

    return milestones;
  }

  function onSubmit(data: GoalFormData) {
    if (editingGoal) {
      updateGoalMutation.mutate({ id: editingGoal._id, data });
    } else {
      createGoalMutation.mutate(data);
    }
  }

  function handleEdit(goal: Goal) {
    setEditingGoal(goal);
    form.reset({
      goalType: goal.goalType,
      title: goal.title,
      description: goal.description || '',
      targetValue: goal.targetValue,
      currentValue: goal.currentValue,
      unit: goal.unit,
      targetDate: goal.targetDate ? format(new Date(goal.targetDate), 'yyyy-MM-dd') : '',
    });
    setIsCreateOpen(true);
  }

  function handleUpdateProgress(goal: Goal, newValue: number) {
    updateProgressMutation.mutate({ id: goal._id, currentValue: newValue });
  }

  function getGoalTypeColor(type: string) {
    switch (type) {
      case 'weight': return 'bg-blue-500';
      case 'fitness': return 'bg-green-500';
      case 'nutrition': return 'bg-orange-500';
      default: return 'bg-gray-500';
    }
  }

  function getGoalTypeIcon(type: string) {
    switch (type) {
      case 'weight': return TrendingUp;
      case 'fitness': return Target;
      case 'nutrition': return Award;
      default: return Target;
    }
  }

  if (isLoading || !clientId) {
    return (
      <div className="min-h-screen flex flex-col">
        <ClientHeader currentPage="goals" />
        <main className="flex-1 py-8">
          <div className="container mx-auto px-6">
            <Skeleton className="h-10 w-64 mb-8" />
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {[...Array(3)].map((_, i) => (
                <Skeleton key={i} className="h-64" />
              ))}
            </div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col">
      <ClientHeader currentPage="goals" />

      <main className="flex-1 py-8">
        <div className="container mx-auto px-6 space-y-8">
          <div className="flex items-center justify-between gap-4 flex-wrap">
            <div>
              <h1 className="text-3xl font-display font-bold tracking-tight" data-testid="text-goals-title">
                My Goals
              </h1>
              <p className="text-muted-foreground mt-1">
                Track your fitness journey with personalized goals
              </p>
            </div>
            <Dialog open={isCreateOpen} onOpenChange={(open) => {
              setIsCreateOpen(open);
              if (!open) {
                setEditingGoal(null);
                form.reset();
              }
            }}>
              <DialogTrigger asChild>
                <Button data-testid="button-create-goal">
                  <Plus className="h-4 w-4 mr-2" />
                  Create Goal
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-[500px]">
                <DialogHeader>
                  <DialogTitle>{editingGoal ? 'Edit Goal' : 'Create New Goal'}</DialogTitle>
                  <DialogDescription>
                    Set up a new goal to track your progress
                  </DialogDescription>
                </DialogHeader>
                <Form {...form}>
                  <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                    <FormField
                      control={form.control}
                      name="goalType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Goal Type</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-goal-type">
                                <SelectValue placeholder="Select goal type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="weight">Weight Goal</SelectItem>
                              <SelectItem value="fitness">Fitness Goal</SelectItem>
                              <SelectItem value="nutrition">Nutrition Goal</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="title"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Title</FormLabel>
                          <FormControl>
                            <Input placeholder="E.g., Lose 10kg" {...field} data-testid="input-goal-title" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="description"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Description (Optional)</FormLabel>
                          <FormControl>
                            <Textarea placeholder="Add details about your goal..." {...field} data-testid="input-goal-description" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <div className="grid grid-cols-3 gap-4">
                      <FormField
                        control={form.control}
                        name="currentValue"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Current</FormLabel>
                            <FormControl>
                              <Input type="number" step="0.1" placeholder="0" {...field} data-testid="input-current-value" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="targetValue"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Target</FormLabel>
                            <FormControl>
                              <Input type="number" step="0.1" placeholder="0" {...field} data-testid="input-target-value" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="unit"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Unit</FormLabel>
                            <FormControl>
                              <Input placeholder="kg, reps, kcal" {...field} data-testid="input-unit" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <FormField
                      control={form.control}
                      name="targetDate"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Target Date (Optional)</FormLabel>
                          <FormControl>
                            <Input type="date" {...field} data-testid="input-target-date" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <DialogFooter>
                      <Button
                        type="submit"
                        disabled={createGoalMutation.isPending || updateGoalMutation.isPending}
                        data-testid="button-submit-goal"
                      >
                        {editingGoal ? 'Update Goal' : 'Create Goal'}
                      </Button>
                    </DialogFooter>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
          </div>

          {goals && goals.length === 0 ? (
            <Card className="text-center py-12">
              <CardContent className="space-y-4">
                <Target className="h-16 w-16 mx-auto text-muted-foreground" />
                <div>
                  <h3 className="text-xl font-semibold">No goals yet</h3>
                  <p className="text-muted-foreground mt-2">
                    Create your first goal to start tracking your progress
                  </p>
                </div>
                <Button onClick={() => setIsCreateOpen(true)} data-testid="button-create-first-goal">
                  <Plus className="h-4 w-4 mr-2" />
                  Create Your First Goal
                </Button>
              </CardContent>
            </Card>
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {goals?.map((goal) => {
                const Icon = getGoalTypeIcon(goal.goalType);
                const isCompleted = goal.status === 'completed';
                const daysRemaining = goal.targetDate 
                  ? Math.ceil((new Date(goal.targetDate).getTime() - Date.now()) / (1000 * 60 * 60 * 24))
                  : null;

                return (
                  <Card key={goal._id} data-testid={`card-goal-${goal._id}`}>
                    <CardHeader className="gap-1 space-y-0">
                      <div className="flex items-start justify-between gap-2">
                        <div className="flex items-center gap-2">
                          <div className={`p-2 rounded-md ${getGoalTypeColor(goal.goalType)}`}>
                            <Icon className="h-4 w-4 text-white" />
                          </div>
                          <div>
                            <CardTitle className="text-lg">{goal.title}</CardTitle>
                            <Badge variant="outline" className="mt-1">
                              {goal.goalType}
                            </Badge>
                          </div>
                        </div>
                        {isCompleted && (
                          <CheckCircle2 className="h-5 w-5 text-green-500" data-testid="icon-goal-completed" />
                        )}
                      </div>
                      {goal.description && (
                        <CardDescription className="mt-2">{goal.description}</CardDescription>
                      )}
                    </CardHeader>
                    <CardContent className="space-y-4">
                      <div>
                        <div className="flex items-center justify-between gap-2 mb-2">
                          <span className="text-sm font-medium">Progress</span>
                          <span className="text-sm text-muted-foreground">
                            {goal.currentValue} / {goal.targetValue} {goal.unit}
                          </span>
                        </div>
                        <Progress value={goal.progress} className="h-2" data-testid={`progress-goal-${goal._id}`} />
                        <div className="flex items-center justify-between gap-2 mt-1">
                          <span className="text-xs text-muted-foreground">{goal.progress}% complete</span>
                          {daysRemaining !== null && daysRemaining > 0 && (
                            <span className="text-xs text-muted-foreground flex items-center gap-1">
                              <Calendar className="h-3 w-3" />
                              {daysRemaining} days left
                            </span>
                          )}
                        </div>
                      </div>

                      {goal.milestones.length > 0 && (
                        <div className="space-y-2">
                          <span className="text-sm font-medium">Milestones</span>
                          <div className="space-y-1">
                            {goal.milestones.map((milestone, idx) => (
                              <div key={idx} className="flex items-center gap-2 text-xs">
                                {milestone.achieved ? (
                                  <CheckCircle2 className="h-3 w-3 text-green-500 flex-shrink-0" />
                                ) : (
                                  <div className="h-3 w-3 rounded-full border border-muted-foreground flex-shrink-0" />
                                )}
                                <span className={milestone.achieved ? 'text-muted-foreground line-through' : ''}>
                                  {milestone.label}
                                </span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      <div className="flex gap-2 pt-2">
                        <Dialog>
                          <DialogTrigger asChild>
                            <Button variant="outline" size="sm" className="flex-1" data-testid={`button-update-progress-${goal._id}`}>
                              <TrendingUp className="h-3 w-3 mr-1" />
                              Update
                            </Button>
                          </DialogTrigger>
                          <DialogContent>
                            <DialogHeader>
                              <DialogTitle>Update Progress</DialogTitle>
                              <DialogDescription>
                                Update your current progress for: {goal.title}
                              </DialogDescription>
                            </DialogHeader>
                            <div className="space-y-4">
                              <div>
                                <label className="text-sm font-medium">Current Value</label>
                                <Input
                                  type="number"
                                  step="0.1"
                                  defaultValue={goal.currentValue}
                                  onChange={(e) => {
                                    const newValue = parseFloat(e.target.value);
                                    if (!isNaN(newValue)) {
                                      handleUpdateProgress(goal, newValue);
                                    }
                                  }}
                                  data-testid="input-update-progress"
                                />
                                <p className="text-xs text-muted-foreground mt-1">
                                  Target: {goal.targetValue} {goal.unit}
                                </p>
                              </div>
                            </div>
                          </DialogContent>
                        </Dialog>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEdit(goal)}
                          data-testid={`button-edit-goal-${goal._id}`}
                        >
                          <Edit className="h-3 w-3" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => deleteGoalMutation.mutate(goal._id)}
                          data-testid={`button-delete-goal-${goal._id}`}
                        >
                          <Trash2 className="h-3 w-3" />
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

client/src/pages/support-tickets.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useLanguage } from '@/lib/language-context';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { queryClient, apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { AlertCircle, CheckCircle2, Clock, Plus, TicketIcon } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';

interface Ticket {
  _id: string;
  ticketNumber: string;
  clientId: string;
  clientName: string;
  subject: string;
  category: 'technical' | 'billing' | 'account' | 'training' | 'general';
  priority: 'low' | 'medium' | 'high' | 'urgent';
  status: 'open' | 'in-progress' | 'waiting-response' | 'resolved' | 'closed';
  description: string;
  attachments?: Array<{
    type: string;
    url: string;
    name: string;
  }>;
  responses: Array<{
    responderId: string;
    responderName: string;
    responderType: 'client' | 'support' | 'admin';
    message: string;
    attachments?: Array<{
      type: string;
      url: string;
      name: string;
    }>;
    createdAt: string;
  }>;
  assignedTo?: string;
  assignedToName?: string;
  createdAt: string;
  resolvedAt?: string;
  closedAt?: string;
}

const ticketSchema = z.object({
  subject: z.string().min(5, 'Subject must be at least 5 characters'),
  category: z.string().min(1, 'Please select a category'),
  priority: z.string().min(1, 'Please select a priority'),
  description: z.string().min(20, 'Description must be at least 20 characters'),
});

type TicketFormData = z.infer<typeof ticketSchema>;

export default function SupportTicketsPage() {
  const { t } = useLanguage();
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedTicket, setSelectedTicket] = useState<Ticket | null>(null);

  const clientId = '67370beb3a67ba69a1f13bb4'; // Demo client ID

  const { data: tickets, isLoading } = useQuery<Ticket[]>({
    queryKey: ['/api/tickets/client', clientId],
  });

  const form = useForm<TicketFormData>({
    resolver: zodResolver(ticketSchema),
    defaultValues: {
      subject: '',
      category: '',
      priority: '',
      description: '',
    },
  });

  const createTicketMutation = useMutation({
    mutationFn: async (data: TicketFormData) => {
      return await apiRequest('/api/tickets', 'POST', {
        ...data,
        clientId,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tickets/client', clientId] });
      setIsDialogOpen(false);
      form.reset();
      toast({
        title: t('common.success'),
        description: 'Ticket created successfully',
      });
    },
    onError: () => {
      toast({
        title: t('common.error'),
        description: t('msg.error'),
        variant: 'destructive',
      });
    },
  });

  const onSubmit = (data: TicketFormData) => {
    createTicketMutation.mutate(data);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'resolved':
        return <CheckCircle2 className="w-5 h-5 text-green-500" data-testid="icon-resolved" />;
      case 'in-progress':
        return <Clock className="w-5 h-5 text-blue-500" data-testid="icon-in-progress" />;
      case 'waiting-response':
        return <Clock className="w-5 h-5 text-orange-500" data-testid="icon-waiting" />;
      case 'closed':
        return <CheckCircle2 className="w-5 h-5 text-muted-foreground" data-testid="icon-closed" />;
      default:
        return <AlertCircle className="w-5 h-5 text-orange-500" data-testid="icon-open" />;
    }
  };

  const getStatusVariant = (status: string) => {
    switch (status) {
      case 'resolved':
        return 'default';
      case 'in-progress':
        return 'secondary';
      case 'waiting-response':
        return 'outline';
      case 'closed':
        return 'outline';
      default:
        return 'destructive';
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'open':
        return t('comm.statusOpen');
      case 'in-progress':
        return t('comm.statusInProgress');
      case 'waiting-response':
        return t('comm.statusWaitingResponse');
      case 'resolved':
        return t('comm.statusResolved');
      case 'closed':
        return t('comm.statusClosed');
      default:
        return status;
    }
  };

  const getPriorityBadge = (priority: string) => {
    let variant: 'default' | 'secondary' | 'destructive' | 'outline' = 'secondary';
    let label = t('comm.priorityLow');

    if (priority === 'urgent') {
      variant = 'destructive';
      label = t('comm.priorityUrgent');
    } else if (priority === 'high') {
      variant = 'destructive';
      label = t('comm.priorityHigh');
    } else if (priority === 'medium') {
      variant = 'default';
      label = t('comm.priorityMedium');
    } else if (priority === 'low') {
      variant = 'secondary';
      label = t('comm.priorityLow');
    }

    return (
      <Badge variant={variant} data-testid={`badge-priority-${priority}`}>
        {label}
      </Badge>
    );
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full" data-testid="loading-tickets">
        <div className="text-center">
          <div className="text-lg font-medium">{t('common.loading')}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full overflow-auto p-6">
      <div className="max-w-4xl mx-auto space-y-6">
        <div className="flex flex-wrap items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold" data-testid="text-page-title">{t('comm.supportTickets')}</h1>
            <p className="text-muted-foreground mt-1">
              {tickets && tickets.length > 0
                ? `${tickets.length} ${tickets.length === 1 ? 'ticket' : 'tickets'}`
                : t('comm.noTickets')}
            </p>
          </div>

          <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
            <DialogTrigger asChild>
              <Button data-testid="button-create-ticket">
                <Plus className="w-4 h-4 mr-2" />
                {t('comm.createTicket')}
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle data-testid="text-dialog-title">{t('comm.newTicket')}</DialogTitle>
                <DialogDescription>
                  Fill out the form below to submit a support ticket
                </DialogDescription>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="subject"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>{t('comm.ticketSubject')}</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="Brief description of the issue" 
                            {...field} 
                            data-testid="input-subject"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="category"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>{t('comm.ticketCategory')}</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-category">
                                <SelectValue placeholder="Select category" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="general">{t('comm.categoryGeneral')}</SelectItem>
                              <SelectItem value="technical">{t('comm.categoryTechnical')}</SelectItem>
                              <SelectItem value="billing">{t('comm.categoryBilling')}</SelectItem>
                              <SelectItem value="account">{t('comm.categoryAccount')}</SelectItem>
                              <SelectItem value="training">{t('comm.categoryTraining')}</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="priority"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>{t('comm.ticketPriority')}</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-priority">
                                <SelectValue placeholder="Select priority" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="low">{t('comm.priorityLow')}</SelectItem>
                              <SelectItem value="medium">{t('comm.priorityMedium')}</SelectItem>
                              <SelectItem value="high">{t('comm.priorityHigh')}</SelectItem>
                              <SelectItem value="urgent">{t('comm.priorityUrgent')}</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>{t('comm.ticketDescription')}</FormLabel>
                        <FormControl>
                          <Textarea 
                            placeholder="Provide detailed information about your issue..." 
                            rows={6}
                            {...field} 
                            data-testid="textarea-description"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <DialogFooter>
                    <Button 
                      type="submit" 
                      disabled={createTicketMutation.isPending}
                      data-testid="button-submit-ticket"
                    >
                      {createTicketMutation.isPending ? t('common.loading') : t('comm.submitTicket')}
                    </Button>
                  </DialogFooter>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>

        {(!tickets || tickets.length === 0) && (
          <Card data-testid="card-no-tickets">
            <CardContent className="flex flex-col items-center justify-center py-12">
              <TicketIcon className="w-16 h-16 text-muted-foreground mb-4" />
              <p className="text-lg font-medium text-muted-foreground">{t('comm.noTickets')}</p>
              <p className="text-sm text-muted-foreground mt-2">
                Create your first support ticket to get help
              </p>
            </CardContent>
          </Card>
        )}

        <div className="space-y-4">
          {tickets?.map((ticket) => (
            <Card 
              key={ticket._id} 
              className="hover-elevate cursor-pointer"
              onClick={() => setSelectedTicket(ticket)}
              data-testid={`card-ticket-${ticket._id}`}
            >
              <CardHeader>
                <div className="flex items-start justify-between gap-4 flex-wrap">
                  <div className="flex items-start gap-3 flex-1 min-w-0">
                    {getStatusIcon(ticket.status)}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2 flex-wrap mb-2">
                        <CardTitle className="text-xl" data-testid={`text-title-${ticket._id}`}>
                          {ticket.subject}
                        </CardTitle>
                        <Badge variant="outline" data-testid={`badge-ticket-number-${ticket._id}`}>
                          {ticket.ticketNumber}
                        </Badge>
                      </div>
                      <CardDescription className="line-clamp-2">
                        {ticket.description}
                      </CardDescription>
                    </div>
                  </div>
                  <div className="flex flex-wrap gap-2">
                    <Badge variant={getStatusVariant(ticket.status)} data-testid={`badge-status-${ticket._id}`}>
                      {getStatusLabel(ticket.status)}
                    </Badge>
                    {getPriorityBadge(ticket.priority)}
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <div className="flex items-center justify-between text-sm text-muted-foreground">
                  <span data-testid={`text-date-${ticket._id}`}>
                    Created {formatDistanceToNow(new Date(ticket.createdAt), { addSuffix: true })}
                  </span>
                  {ticket.responses && ticket.responses.length > 0 && (
                    <span data-testid={`text-responses-${ticket._id}`}>
                      {ticket.responses.length} {ticket.responses.length === 1 ? 'response' : 'responses'}
                    </span>
                  )}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
}

server/models/announcement.ts.content:
import mongoose, { Document, Schema } from 'mongoose';

export interface IAnnouncement extends Document {
  title: string;
  content: string;
  category: 'general' | 'maintenance' | 'event' | 'promotion' | 'policy' | 'emergency';
  priority: 'low' | 'normal' | 'high' | 'urgent';
  authorId: string;
  authorName: string;
  targetAudience: 'all' | 'basic' | 'premium' | 'elite';
  images?: string[];
  isPinned: boolean;
  expiresAt?: Date;
  createdAt: Date;
  updatedAt: Date;
}

const announcementSchema = new Schema<IAnnouncement>({
  title: { type: String, required: true },
  content: { type: String, required: true },
  category: { 
    type: String, 
    enum: ['general', 'maintenance', 'event', 'promotion', 'policy', 'emergency'],
    default: 'general'
  },
  priority: { 
    type: String, 
    enum: ['low', 'normal', 'high', 'urgent'],
    default: 'normal'
  },
  authorId: { type: String, required: true },
  authorName: { type: String, required: true },
  targetAudience: { 
    type: String, 
    enum: ['all', 'basic', 'premium', 'elite'],
    default: 'all'
  },
  images: [String],
  isPinned: { type: Boolean, default: false },
  expiresAt: Date
}, {
  timestamps: true
});

announcementSchema.index({ isPinned: -1, createdAt: -1 });
announcementSchema.index({ targetAudience: 1, createdAt: -1 });

export const Announcement = mongoose.model<IAnnouncement>('Announcement', announcementSchema);

server/models/message.ts.content:
import mongoose, { Document, Schema } from 'mongoose';

export interface IMessage extends Document {
  conversationId: string;
  senderId: string;
  senderType: 'client' | 'trainer' | 'admin';
  senderName: string;
  receiverId: string;
  receiverType: 'client' | 'trainer' | 'admin';
  receiverName: string;
  content: string;
  attachments?: {
    type: 'image' | 'file' | 'video';
    url: string;
    name: string;
  }[];
  isRead: boolean;
  readAt?: Date;
  createdAt: Date;
  updatedAt: Date;
}

const messageSchema = new Schema<IMessage>({
  conversationId: { type: String, required: true, index: true },
  senderId: { type: String, required: true, index: true },
  senderType: { type: String, enum: ['client', 'trainer', 'admin'], required: true },
  senderName: { type: String, required: true },
  receiverId: { type: String, required: true, index: true },
  receiverType: { type: String, enum: ['client', 'trainer', 'admin'], required: true },
  receiverName: { type: String, required: true },
  content: { type: String, required: true },
  attachments: [{
    type: { type: String, enum: ['image', 'file', 'video'] },
    url: String,
    name: String
  }],
  isRead: { type: Boolean, default: false },
  readAt: Date
}, {
  timestamps: true
});

messageSchema.index({ conversationId: 1, createdAt: -1 });
messageSchema.index({ senderId: 1, receiverId: 1 });

export const Message = mongoose.model<IMessage>('Message', messageSchema);

server/models/ticket.ts.content:
import mongoose, { Document, Schema } from 'mongoose';

export interface ITicket extends Document {
  ticketNumber: string;
  clientId: string;
  clientName: string;
  subject: string;
  category: 'technical' | 'billing' | 'account' | 'training' | 'general';
  priority: 'low' | 'medium' | 'high' | 'urgent';
  status: 'open' | 'in-progress' | 'waiting-response' | 'resolved' | 'closed';
  description: string;
  attachments?: {
    type: string;
    url: string;
    name: string;
  }[];
  responses: {
    responderId: string;
    responderName: string;
    responderType: 'client' | 'support' | 'admin';
    message: string;
    attachments?: {
      type: string;
      url: string;
      name: string;
    }[];
    createdAt: Date;
  }[];
  assignedTo?: string;
  assignedToName?: string;
  resolvedAt?: Date;
  closedAt?: Date;
  createdAt: Date;
  updatedAt: Date;
}

const ticketSchema = new Schema<ITicket>({
  ticketNumber: { type: String, required: true, unique: true },
  clientId: { type: String, required: true, index: true },
  clientName: { type: String, required: true },
  subject: { type: String, required: true },
  category: { 
    type: String, 
    enum: ['technical', 'billing', 'account', 'training', 'general'],
    required: true 
  },
  priority: { 
    type: String, 
    enum: ['low', 'medium', 'high', 'urgent'],
    default: 'medium'
  },
  status: { 
    type: String, 
    enum: ['open', 'in-progress', 'waiting-response', 'resolved', 'closed'],
    default: 'open',
    index: true
  },
  description: { type: String, required: true },
  attachments: [{
    type: String,
    url: String,
    name: String
  }],
  responses: [{
    responderId: { type: String, required: true },
    responderName: { type: String, required: true },
    responderType: { type: String, enum: ['client', 'support', 'admin'], required: true },
    message: { type: String, required: true },
    attachments: [{
      type: String,
      url: String,
      name: String
    }],
    createdAt: { type: Date, default: Date.now }
  }],
  assignedTo: String,
  assignedToName: String,
  resolvedAt: Date,
  closedAt: Date
}, {
  timestamps: true
});

ticketSchema.index({ clientId: 1, status: 1 });

export const Ticket = mongoose.model<ITicket>('Ticket', ticketSchema);

