import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { useLanguage } from '@/lib/language-context';
import { AlertCircle, Bell, Info, Pin } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';

interface Announcement {
  _id: string;
  title: string;
  content: string;
  category: 'general' | 'maintenance' | 'event' | 'promotion' | 'policy' | 'emergency';
  priority: 'low' | 'normal' | 'high' | 'urgent';
  authorId: string;
  authorName: string;
  targetAudience: 'all' | 'basic' | 'premium' | 'elite';
  images?: string[];
  isPinned: boolean;
  createdAt: string;
  expiresAt?: string;
}

export default function AnnouncementsPage() {
  const { t } = useLanguage();

  const { data: announcements, isLoading } = useQuery<Announcement[]>({
    queryKey: ['/api/announcements'],
  });

  const pinnedAnnouncements = announcements?.filter(a => a.isPinned) || [];
  const regularAnnouncements = announcements?.filter(a => !a.isPinned) || [];

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return <AlertCircle className="w-5 h-5 text-destructive" data-testid="icon-urgent" />;
      case 'high':
        return <Bell className="w-5 h-5 text-orange-500" data-testid="icon-high" />;
      default:
        return <Info className="w-5 h-5 text-blue-500" data-testid="icon-normal" />;
    }
  };

  const getPriorityBadgeVariant = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return 'destructive';
      case 'high':
        return 'default';
      default:
        return 'secondary';
    }
  };

  const getPriorityLabel = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return t('comm.urgent');
      case 'high':
        return t('comm.important');
      default:
        return t('comm.info');
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full" data-testid="loading-announcements">
        <div className="text-center">
          <div className="text-lg font-medium">{t('common.loading')}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full overflow-auto p-6">
      <div className="max-w-4xl mx-auto space-y-6">
        <div className="flex items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold" data-testid="text-page-title">{t('comm.announcementFeed')}</h1>
            <p className="text-muted-foreground mt-1">
              {announcements && announcements.length > 0
                ? `${announcements.length} ${announcements.length === 1 ? 'announcement' : 'announcements'}`
                : t('comm.noAnnouncements')}
            </p>
          </div>
        </div>

        {(!announcements || announcements.length === 0) && (
          <Card data-testid="card-no-announcements">
            <CardContent className="flex flex-col items-center justify-center py-12">
              <Bell className="w-16 h-16 text-muted-foreground mb-4" />
              <p className="text-lg font-medium text-muted-foreground">{t('comm.noAnnouncements')}</p>
            </CardContent>
          </Card>
        )}

        {pinnedAnnouncements.length > 0 && (
          <div className="space-y-4">
            <div className="flex items-center gap-2">
              <Pin className="w-5 h-5 text-primary" />
              <h2 className="text-xl font-semibold">{t('comm.pinned')}</h2>
            </div>

            {pinnedAnnouncements.map((announcement) => (
              <Card 
                key={announcement._id} 
                className="border-l-4 border-l-primary"
                data-testid={`card-announcement-${announcement._id}`}
              >
                <CardHeader>
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex items-start gap-3 flex-1">
                      {getPriorityIcon(announcement.priority)}
                      <div className="flex-1 min-w-0">
                        <CardTitle className="text-xl mb-2" data-testid={`text-title-${announcement._id}`}>
                          {announcement.title}
                        </CardTitle>
                        <div className="flex flex-wrap gap-2">
                          <Badge variant={getPriorityBadgeVariant(announcement.priority)} data-testid={`badge-priority-${announcement._id}`}>
                            {getPriorityLabel(announcement.priority)}
                          </Badge>
                          <Badge variant="outline" data-testid={`badge-pinned-${announcement._id}`}>
                            {t('comm.pinned')}
                          </Badge>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <p className="text-muted-foreground mb-4 whitespace-pre-wrap" data-testid={`text-content-${announcement._id}`}>
                    {announcement.content}
                  </p>
                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <span data-testid={`text-date-${announcement._id}`}>
                      {t('comm.postedOn')} {formatDistanceToNow(new Date(announcement.createdAt), { addSuffix: true })}
                    </span>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {regularAnnouncements.length > 0 && (
          <div className="space-y-4">
            {pinnedAnnouncements.length > 0 && (
              <h2 className="text-xl font-semibold">{t('comm.announcements')}</h2>
            )}

            {regularAnnouncements.map((announcement) => (
              <Card 
                key={announcement._id}
                data-testid={`card-announcement-${announcement._id}`}
              >
                <CardHeader>
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex items-start gap-3 flex-1">
                      {getPriorityIcon(announcement.priority)}
                      <div className="flex-1 min-w-0">
                        <CardTitle className="text-xl mb-2" data-testid={`text-title-${announcement._id}`}>
                          {announcement.title}
                        </CardTitle>
                        <Badge variant={getPriorityBadgeVariant(announcement.priority)} data-testid={`badge-priority-${announcement._id}`}>
                          {getPriorityLabel(announcement.priority)}
                        </Badge>
                      </div>
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <p className="text-muted-foreground mb-4 whitespace-pre-wrap" data-testid={`text-content-${announcement._id}`}>
                    {announcement.content}
                  </p>
                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
                    <span data-testid={`text-date-${announcement._id}`}>
                      {t('comm.postedOn')} {formatDistanceToNow(new Date(announcement.createdAt), { addSuffix: true })}
                    </span>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

server/models/forum.ts.content:
import mongoose, { Document, Schema } from 'mongoose';

export interface IForumTopic extends Document {
  title: string;
  content: string;
  category: 'general' | 'nutrition' | 'workouts' | 'motivation' | 'progress' | 'questions';
  authorId: string;
  authorName: string;
  authorAvatar?: string;
  tags: string[];
  isPinned: boolean;
  isLocked: boolean;
  viewCount: number;
  likeCount: number;
  replies: {
    replyId: string;
    authorId: string;
    authorName: string;
    authorAvatar?: string;
    content: string;
    likeCount: number;
    createdAt: Date;
    updatedAt: Date;
  }[];
  lastActivityAt: Date;
  createdAt: Date;
  updatedAt: Date;
}

const forumTopicSchema = new Schema<IForumTopic>({
  title: { type: String, required: true },
  content: { type: String, required: true },
  category: { 
    type: String, 
    enum: ['general', 'nutrition', 'workouts', 'motivation', 'progress', 'questions'],
    default: 'general',
    index: true
  },
  authorId: { type: String, required: true, index: true },
  authorName: { type: String, required: true },
  authorAvatar: String,
  tags: [String],
  isPinned: { type: Boolean, default: false },
  isLocked: { type: Boolean, default: false },
  viewCount: { type: Number, default: 0 },
  likeCount: { type: Number, default: 0 },
  replies: [{
    replyId: { type: String, required: true },
    authorId: { type: String, required: true },
    authorName: { type: String, required: true },
    authorAvatar: String,
    content: { type: String, required: true },
    likeCount: { type: Number, default: 0 },
    createdAt: { type: Date, default: Date.now },
    updatedAt: { type: Date, default: Date.now }
  }],
  lastActivityAt: { type: Date, default: Date.now }
}, {
  timestamps: true
});

forumTopicSchema.index({ isPinned: -1, lastActivityAt: -1 });
forumTopicSchema.index({ category: 1, lastActivityAt: -1 });

export const ForumTopic = mongoose.model<IForumTopic>('ForumTopic', forumTopicSchema);

client/src/pages/trainer-messaging.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { useLanguage } from '@/lib/language-context';
import { MessageSquare, ExternalLink, Phone } from 'lucide-react';
import { FaWhatsapp } from 'react-icons/fa';

interface TrainerInfo {
  name: string;
  phone: string;
  email?: string;
  specialization?: string;
  bio?: string;
}

export default function TrainerMessaging() {
  const { t } = useLanguage();
  const [clientId, setClientId] = useState<string | null>(null);

  useEffect(() => {
    const id = localStorage.getItem('clientId');
    setClientId(id);
  }, []);

  const { data: client } = useQuery<any>({
    queryKey: ['/api/clients', clientId],
    enabled: !!clientId,
  });

  const trainerInfo: TrainerInfo = {
    name: 'HOC Trainer',
    phone: '+1234567890',
    specialization: 'Strength & Conditioning',
    bio: 'Certified personal trainer with 10+ years of experience helping clients achieve their fitness goals.',
  };

  const handleWhatsAppClick = () => {
    const phoneNumber = trainerInfo.phone.replace(/\D/g, '');
    const message = encodeURIComponent(`Hi ${trainerInfo.name}, I'm ${client?.name || 'a FitPro member'} and I'd like to discuss my training.`);
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${message}`;
    window.open(whatsappUrl, '_blank');
  };

  return (
    <div className="h-full overflow-auto p-6">
      <div className="max-w-4xl mx-auto space-y-6">
        <div>
          <h1 className="text-3xl font-bold" data-testid="text-page-title">{t('comm.trainerMessaging')}</h1>
          <p className="text-muted-foreground mt-1">
            Connect directly with your personal trainer
          </p>
        </div>

        <Card data-testid="card-trainer-info">
          <CardHeader>
            <div className="flex items-start gap-4">
              <Avatar className="h-16 w-16">
                <AvatarFallback className="text-xl bg-primary text-primary-foreground">
                  {trainerInfo.name.split(' ').map(n => n[0]).join('')}
                </AvatarFallback>
              </Avatar>
              <div className="flex-1">
                <CardTitle className="text-2xl mb-1" data-testid="text-trainer-name">
                  {trainerInfo.name}
                </CardTitle>
                <CardDescription className="text-base" data-testid="text-trainer-specialization">
                  {trainerInfo.specialization}
                </CardDescription>
              </div>
            </div>
          </CardHeader>
          <CardContent className="space-y-6">
            <div>
              <h3 className="font-semibold mb-2">About Your Trainer</h3>
              <p className="text-muted-foreground" data-testid="text-trainer-bio">
                {trainerInfo.bio}
              </p>
            </div>

            <div className="border-t pt-6">
              <h3 className="font-semibold mb-4">Contact Your Trainer</h3>
              <div className="space-y-4">
                <div className="flex items-center gap-3 text-muted-foreground">
                  <Phone className="h-5 w-5" />
                  <span data-testid="text-trainer-phone">{trainerInfo.phone}</span>
                </div>

                <Button 
                  onClick={handleWhatsAppClick}
                  className="w-full sm:w-auto gap-2"
                  size="lg"
                  data-testid="button-whatsapp-trainer"
                >
                  <FaWhatsapp className="h-5 w-5" />
                  Chat on WhatsApp
                  <ExternalLink className="h-4 w-4" />
                </Button>

                <p className="text-sm text-muted-foreground">
                  Click the button above to start a direct WhatsApp conversation with your trainer. You can discuss your workout plans, ask questions, and get personalized guidance.
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-messaging-tips">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MessageSquare className="h-5 w-5" />
              Messaging Tips
            </CardTitle>
          </CardHeader>
          <CardContent>
            <ul className="space-y-2 text-muted-foreground">
              <li className="flex items-start gap-2">
                <span className="text-primary mt-1">â€¢</span>
                <span>Be specific about your questions or concerns</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-primary mt-1">â€¢</span>
                <span>Share photos or videos of your form if needed</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-primary mt-1">â€¢</span>
                <span>Your trainer typically responds within 24 hours</span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-primary mt-1">â€¢</span>
                <span>Schedule check-ins to stay on track with your goals</span>
              </li>
            </ul>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
